$rss = new SimpleXMLElement('<rss version="2.0"></rss>');\n$channel = $rss->addChild('channel');\n$item = $channel->addChild('item');\n$item->addChild('title', 'Image with text');\n$item->addChild('link', 'http://example.com/image.jpg');\n$item->addChild('description', 'An image with dynamically added text.');\n$item->addChild('enclosure', 'http://example.com/image.jpg', array('type' => 'image/jpeg'));\n\nheader('Content-Type: application/rss+xml');\necho $rss->asXML();