$image = imagecreatefromjpeg('image.jpg'); // load the image\n$text = 'Hello, World!'; // text to be written\n$font = 'arial.ttf'; // font to use\n$size = 24; // font size\n$color = imagecolorallocate($image, 255, 255, 255); // white color\n\n// write text on the image\nimagettftext($image, $size, 0, 10, 20, $color, $font, $text);\n\n// output the image\nheader('Content-Type: image/jpeg');\nimagejpeg($image);\nimagedestroy($image);