<?php\n\n// Create a FastCGI server\n$fcgi = new FCGI();\n\n// Define a callback function for incoming requests\nfunction handle_request($request) {\n    // Your web app framework logic goes here\n    // This will be executed for every incoming request\n    echo "Hello, World!";\n}\n\n// Register the callback function with the FastCGI server\n$fcgi->register_request_callback('handle_request');\n\n// Start the FastCGI server\n$fcgi->start();\n\n?>