function deleteLinesFromFile($filename, $numLinesToDelete) {\n    $fileContent = file($filename);\n    if (count($fileContent) > $numLinesToDelete + 2) {\n        $newContent = array_slice($fileContent, 0, 2);\n        $newContent = array_merge($newContent, array_slice($fileContent, $numLinesToDelete + 2));\n        file_put_contents($filename, implode('', $newContent));\n    }\n}\n\n// Usage example\n$filename = 'log.txt';\n$numLinesToDelete = 5;\ndeleteLinesFromFile($filename, $numLinesToDelete);