class MyClass {\n    public $value = 'original value';\n\n    public static function getInstance() {\n        return new self();\n    }\n}\n\n// Without the & operator\n$instance = MyClass::getInstance();\n$instance->value = 'new value';\necho MyClass::getInstance()->value; // outputs "original value"\n\n// With the & operator\n$instance = &MyClass::getInstance();\n$instance->value = 'new value';\necho MyClass::getInstance()->value; // outputs "new value"