function send_trackback($trackback_url, $title, $excerpt, $url) {\n  $data = array(\n    'title' => $title,\n    'excerpt' => $excerpt,\n    'url' => $url\n  );\n  \n  $options = array(\n    'http' => array(\n      'method' => 'POST',\n      'content' => http_build_query($data)\n    )\n  );\n  \n  $context = stream_context_create($options);\n  $response = file_get_contents($trackback_url, false, $context);\n  \n  if ($response !== false) {\n    $xml = simplexml_load_string($response);\n    if ($xml->error) {\n      // handle error\n    } else {\n      // trackback sent successfully\n    }\n  } else {\n    // handle error\n  }\n}