// Handle GET requests for retrieving book data\nif ($_SERVER['REQUEST_METHOD'] === 'GET') {\n    $category = $_GET['category'];\n    $books = get_books_by_category($category);\n    echo json_encode($books);\n}\n\n// Handle POST requests for creating new book resources\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    $title = $_POST['title'];\n    $description = $_POST['description'];\n    create_book($title, $description);\n    echo json_encode(['message' => 'Book created successfully']);\n}\n\n// Handle PUT requests for updating existing book resources\nif ($_SERVER['REQUEST_METHOD'] === 'PUT') {\n    $id = $_GET['id'];\n    $title = $_PUT['title'];\n    $description = $_PUT['description'];\n    update_book($id, $title, $description);\n    echo json_encode(['message' => 'Book updated successfully']);\n}\n\n// Handle DELETE requests for deleting book resources\nif ($_SERVER['REQUEST_METHOD'] === 'DELETE') {\n    $id = $_GET['id'];\n    delete_book($id);\n    echo json_encode(['message' => 'Book deleted successfully']);\n}