// Enable APC\napc.enabled = 1\napc.enable_cli = 1\n\n// Set the cache timeout to 500 requests\napc.ttl = 500\n\n// Load your application code\nrequire_once 'my_app.php';\n\n// Define a function to handle HTTP requests\nfunction myHandleHTTPRequest($url) {\n  // Use your application code to handle the request\n  my_app_handle_request($url);\n}\n\n// Register the function with FastCGI\nfcgi_set_request_handler('myHandleHTTPRequest');