function combine_js_files($files) {\n  $combined_js = '';\n  foreach ($files as $file) {\n    $combined_js .= file_get_contents($file);\n  }\n  return $combined_js;\n}\n\nfunction compress_js($js_code) {\n  require_once 'jsmin/jsmin.php';\n  return JSMin::minify($js_code);\n}\n\n$js_files = glob('includes/js/*.js');\n$combined_js = combine_js_files($js_files);\n$compressed_js = compress_js($combined_js);\n\nheader('Content-Type: application/javascript');\necho $compressed_js;