$conn = oci_connect('username', 'password', ' hostname/service_name');\n\n// Create temporary tables\noci_parse($conn, "CREATE GLOBAL TEMPORARY TABLE temp_table_a (...) ON COMMIT PRESERVE ROWS");\noci_execute($stmt);\n\n// Populate temporary tables\noci_parse($conn, "INSERT INTO temp_table_a (...) VALUES (...)");\noci_execute($stmt);\n\n// Create new partition and exchange with original table\noci_parse($conn, "ALTER TABLE table_a ADD PARTITION p_new VALUES LESS THAN (MAXVALUE)");\noci_execute($stmt);\noci_parse($conn, "ALTER TABLE table_a EXCHANGE PARTITION p_new WITH temp_table_a");\noci_execute($stmt);\n\n// Drop temporary tables\noci_parse($conn, "DROP TABLE temp_table_a");\noci_execute($stmt);\n\noci_close($conn);