class WebSiteUser {\n    private static $common_homepage_content;\n\n    public static function getCommonHomepageContent() {\n        if (self::$common_homepage_content === null) {\n            $result = pg_query("SELECT common_homepage_content FROM web_site_settings WHERE id = 1");\n            self::$common_homepage_content = pg_fetch_result($result, 0, 'common_homepage_content');\n        }\n        return self::$common_homepage_content;\n    }\n}