function top_menu_logged_in_sub_menu($user, $page = 'main') {\n    $menuItems = [\n        ['page' => 'main', 'title' => 'My Admin', 'link' => 'main.php'],\n        ['page' => 'myProjects', 'title' => 'My Projects', 'link' => 'myProjects.php'],\n        ['page' => 'insertProject', 'title' => 'Post a Project', 'link' => 'insertProject.php'],\n        ['page' => 'myAccount', 'title' => 'My Account', 'link' => 'myAccount.php'],\n    ];\n\n    $subMenuHtml = '<div id="sub_nav"><ul>';\n    foreach ($menuItems as $menuItem) {\n        $activeClass = ($menuItem['page'] === $page) ? 'sub_link_active' : 'sub_link';\n        $subMenuHtml .= <<<HTML\n            <li><img src="images/nav_2_lev_tab_Hi_L.gif" alt="" /></li>\n            <li class="$activeClass"><span><a href="{$menuItem['link']}">{$menuItem['title']}</a></span></li>\n            <li><img src="images/nav_2_lev_tab_Hi_R.gif" alt="" /></li>\nHTML;\n    }\n\n    $subMenuHtml .= <<<HTML\n            </ul>\n            <div class="user_id">\n                Welcome, $user! &nbsp;&nbsp;<span class="sign_out"><a href="http://www.example.org/login.php?action=logout">Sign Out</a></span>&nbsp;&nbsp;\n            </div>\n        </div>\nHTML;\n\n    return $subMenuHtml;\n}