$sql = "\n    SELECT \n        page,\n        SUM(lastOnline > $last1Min) as last1Min,\n        SUM(lastOnline > $last5Mins) as last5Mins,\n        SUM(lastOnline > $last1Hr) as last1Hr,\n        SUM(lastOnline > $last1Dy) as last1Dy,\n        SUM(lastOnline > $last1Wk) as last1Wk,\n        SUM(lastOnline > $last1Mnth) as last1Mnth\n    FROM usersonline\n    GROUP BY page\n";\n\n$result = mysql_query($sql);\nwhile ($rows = mysql_fetch_assoc($result)) {\n    echo "Page: " . $rows['page'] . "<br />\n";\n    echo "Users online in the last minute: " . $rows['last1Min'] . "<br />\n";\n    echo "Users online in the last 5 minutes: " . $rows['last5Mins'] . "<br />\n";\n    echo "Users online in the last hour: " . $rows['last1Hr'] . "<br />\n";\n    echo "Users online in the last day: " . $rows['last1Dy'] . "<br />\n";\n    echo "Users online in the last week: " . $rows['last1Wk'] . "<br />\n";\n    echo "Users online in the last month: " . $rows['last1Mnth'] . "<br /><br />\n";\n}