$permissions = array(\n    'read' => 1,   // 00000001\n    'write' => 2,  // 00000010\n    'delete' => 4, // 00000100\n    // ...\n);\n\n$glob = 0; // Initialize the glob to 0\n\n// Add permissions to the glob\n$glob |= $permissions['read'];\n$glob |= $permissions['write'];\n\n// Test if a permission is set\n$test = $permissions['read'];\nif ($glob & $test == $test) {\n    echo "Read permission is set";\n}\n\n$test = $permissions['delete'];\nif ($glob & $test != $test) {\n    echo "Delete permission is not set";\n}