// image_proxy.php\n\n// Check if user is logged in\nif (!isset($_SESSION['username'])) {\n    header('HTTP/1.1 403 Forbidden');\n    exit;\n}\n\n// Get the image path from the request\n$image_path = $_GET['image'];\n\n// Validate the image path (e.g., check if it's a valid image file)\nif (!preg_match('/^[a-zA-Z0-9_\-\.]+\.(jpg|png|gif)$/', $image_path)) {\n    header('HTTP/1.1 404 Not Found');\n    exit;\n}\n\n// Serve the image\nheader('Content-Type: image/jpeg'); // or image/png, image/gif, etc.\nreadfile('/var/www/images/' . $image_path);