<?php\n// Configuration\n$db_host = 'your_host';\n$db_username = 'your_username';\n$db_password = 'your_password';\n$db_name = 'your_database';\n\n// Connect to database\n$conn = new mysqli($db_host, $db_username, $db_password, $db_name);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Create comments table if it doesn't exist\n$query = "CREATE TABLE IF NOT EXISTS comments (\n    id INT PRIMARY KEY AUTO_INCREMENT,\n    comment TEXT,\n    username VARCHAR(50),\n    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP\n)";\n$conn->query($query);\n\n// Function to display comments\nfunction display_comments($conn) {\n    $query = "SELECT * FROM comments ORDER BY created_at DESC";\n    $result = $conn->query($query);\n    while ($row = $result->fetch_assoc()) {\n        echo '<div class="comment">';\n        echo '<span class="username">' . $row['username'] . '</span>';\n        echo '<span class="comment-text">' . nl2br($row['comment']) . '</span>';\n        echo '<span class="created-at">' . $row['created_at'] . '</span>';\n        echo '<a href="#" class="reply">Reply</a>';\n        echo '</div>';\n    }\n}\n\n// Function to add comment\nfunction add_comment($conn, $comment, $username) {\n    $query = "INSERT INTO comments (comment, username) VALUES (?, ?)";\n    $stmt = $conn->prepare($query);\n    $stmt->bind_param("ss", $comment, $username);\n    $stmt->execute();\n}\n\n// Function to validate input\nfunction validate_input($input) {\n    $input = trim($input);\n    $input = htmlspecialchars($input);\n    return $input;\n}\n\n// Handle form submission\nif ($_SERVER['REQUEST_METHOD'] == 'POST') {\n    $comment = validate_input($_POST['comment']);\n    $username = validate_input($_POST['username']);\n    add_comment($conn, $comment, $username);\n}\n\n// Display comments\ndisplay_comments($conn);\n\n// Close database connection\n$conn->close();\n?>