function getPrimes($limit) {\n    $primes = array();\n    $sieve = array_fill(0, ($limit - 1) / 2 + 1, true);\n    $sieve[0] = false;\n\n    for ($i = 1; $i < sqrt($limit) / 2 + 1; $i++) {\n        if ($sieve[$i]) {\n            for ($j = 2 * $i * ($i + 1); $j < count($sieve); $j += 2 * $i + 1) {\n                $sieve[$j] = false;\n            }\n        }\n    }\n\n    foreach ($sieve as $index => $value) {\n        if ($value) {\n            $primes[] = 2 * $index + 1;\n        }\n    }\n\n    return $primes;\n}