function executeQuery($sql) {\n    $result = mysql_query($sql);\n    if (!$result) {\n        if ($_SESSION['auto_id'] == 1) {\n            die("<span style='FONT-SIZE:11px; FONT-COLOR: #000000; font-family=tahoma;'><center>An Internal Error has Occured. Please report following error to error@friendproject.com<br><br>" . $sql . "<br><br>" . mysql_error() . "'</center></FONT>");\n        } else {\n            die("<span style='FONT-SIZE:11px; FONT-COLOR: #000000; font-family=tahoma;'><center>An Internal Error has Occured. The error has been recorded<br>'</center></FONT>");\n        }\n    }\n    return $result;\n}