$countries = array(\n    "217" => "Turkmenistan",\n    "218" => "Turks and Caicos Islands",\n    "219" => "Tuvalu",\n    "220" => "Uganda",\n    "221" => "Ukraine",\n    "222" => "United Arab Emirates",\n    "223" => "United Kingdom (Great Britain)",\n    "224" => "United States"\n);\n\n$userCountryId = $_SESSION['user_country_id']; // or get it from a cookie\n\n<select name="country" style="width:180px;" onChange="do_get_rest_popup(this.value)">\n    <?php foreach ($countries as $countryId => $countryName) { ?>\n        <option value="<?php echo $countryId; ?>" <?php if ($countryId == $userCountryId) { echo "SELECTED"; } ?>><?php echo $countryName; ?></option>\n    <?php } ?>\n</select>