trait PreparedStatementCache {\n    private $statementCache = [];\n\n    protected function prepareStatement($query) {\n        if (!isset($this->statementCache[$query])) {\n            $this->statementCache[$query] = $this->pdo->prepare($query);\n        }\n        return $this->statementCache[$query];\n    }\n}\n\nclass Product {\n    use PreparedStatementCache;\n\n    public function getPrice($id) {\n        $stmt = $this->prepareStatement('SELECT price FROM products WHERE id = ?');\n        $stmt->execute([$id]);\n        return $stmt->fetchColumn(0);\n    }\n}