function generateUlLi($tree) {\n    $output = '';\n    foreach ($tree as $node) {\n        if (isset($node['value'])) {\n            $output .= '<li>';\n            if (is_array($node['value'])) {\n                $output .= '<ul>';\n                $output .= generateUlLi($node['value']);\n                $output .= '</ul>';\n            } else {\n                $output .= $node['value'];\n            }\n            $output .= '</li>';\n        } else {\n            if ($node['tag'] == 'Title') {\n                $output .= '<li>' . $node['value'] . '</li>';\n            }\n        }\n    }\n    return $output;\n}\n\n$tree = array(\n    0 => array(\n        'tag' => 'NavigationMode',\n        'value' => array(\n            0 => array(\n                'tag' => 'Title',\n                'value' => 'Introduction'\n            ),\n            1 => array(\n                'tag' => 'NavigationNode',\n                'value' => array(\n                    0 => array(\n                        'tag' => 'Title',\n                        'value' => 'Sub Intro'\n                    )\n                )\n            )\n        )\n    ),\n    1 => array(\n        'tag' => 'NavigationMode',\n        'value' => array(\n            0 => array(\n                'tag' => 'Title',\n                'value' => 'Module 1'\n            )\n        )\n    )\n);\n\necho '<ul>' . generateUlLi($tree) . '</ul>';