public function soapTest(){\n    $wsdl = "https://cwi.rezexchange.com:9991/?wsdl";\n    $client = new SoapClient($wsdl, array('trace' => 1));\n\n    $params = array(\n        'PropertyCode' => $PCode,\n        'DateFrom' => date('Y-m-d\TH:i:s', strtotime($DateFrom)), // assuming Y-m-d H:i:s format\n        'DateTo' => date('Y-m-d\TH:i:s', strtotime($DateTo)), // assuming Y-m-d H:i:s format\n        'RatePlan' => $RatePlan,\n        'RoomType' => $RoomType,\n        'PromotionalRates' => false\n    );\n\n    try {\n        $result = $client->CwiRateDetails($params);\n        echo($result);\n    } catch (SoapFault $e) {\n        echo "SOAP Fault: " . $e->getMessage();\n    }\n}