// Set API endpoint and credentials\n$apiEndpoint = 'https://your-chevereto-instance.com/api/upload';\n$apiKey = 'your-api-key';\n\n// Set image file and metadata\n$imageFile = 'path/to/image.jpg';\n$imageMetadata = array(\n    'title' => 'Example Image',\n    'description' => 'This is an example image'\n);\n\n// Initialize curl\n$ch = curl_init($apiEndpoint);\n\n// Set curl options\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_POST, true);\ncurl_setopt($ch, CURLOPT_POSTFIELDS, array(\n    'image' => curl_file_create($imageFile),\n    'metadata' => json_encode($imageMetadata)\n));\ncurl_setopt($ch, CURLOPT_HTTPHEADER, array(\n    'Authorization: Bearer ' . $apiKey\n));\n\n// Execute curl request\n$response = curl_exec($ch);\n\n// Get shortened URL from response\n$responseData = json_decode($response, true);\n$shortenedUrl = $responseData['url'];\n\necho $shortenedUrl;