$perms = fileperms('/path/to/your/file');\nif (($perms & 0xC000) == 0xC000) {\n    // Socket\n    $info = 's';\n} elseif (($perms & 0xA000) == 0xA000) {\n    // Symbolic Link\n    $info = 'l';\n} elseif (($perms & 0x8000) == 0x8000) {\n    // Regular\n    $info = '-';\n} elseif (($perms & 0x6000) == 0x6000) {\n    // Block special\n    $info = 'b';\n} elseif (($perms & 0x4000) == 0x4000) {\n    // Directory\n    $info = 'd';\n} elseif (($perms & 0x2000) == 0x2000) {\n    // Character special\n    $info = 'c';\n} elseif (($perms & 0x1000) == 0x1000) {\n    // FIFO pipe\n    $info = 'p';\n} else {\n    // Unknown\n    $info = 'u';\n}\n\n// Owner\n$info .= (($perms & 0x0100) ? 'r' : '-');\n$info .= (($perms & 0x0080) ? 'w' : '-');\n$info .= (($perms & 0x0040) ? 'x' : '-');\n\n// Group\n$info .= (($perms & 0x0020) ? 'r' : '-');\n$info .= (($perms & 0x0010) ? 'w' : '-');\n$info .= (($perms & 0x0008) ? 'x' : '-');\n\n// Other\n$info .= (($perms & 0x0004) ? 'r' : '-');\n$info .= (($perms & 0x0002) ? 'w' : '-');\n$info .= (($perms & 0x0001) ? 'x' : '-');\n\nprint $info;