<?php\n  // Connect to your database\n  $conn = mysqli_connect("your_host", "your_username", "your_password", "your_database");\n\n  // Check connection\n  if (!$conn) {\n    die("Connection failed: " . mysqli_connect_error());\n  }\n\n  // Update the table\n  $sql = "UPDATE your_table SET email_received = 'yes' WHERE email = '".$_GET['email']."'";\n  mysqli_query($conn, $sql);\n\n  // Display the updated content\n  $sql = "SELECT * FROM your_table WHERE email = '".$_GET['email']."'";\n  $result = mysqli_query($conn, $sql);\n  $row = mysqli_fetch_assoc($result);\n  echo "Email received: ".$row['email_received'];\n\n  // Close the connection\n  mysqli_close($conn);\n?>