$target = "images/";\n$target = $target . basename($_FILES['photo']['name']);\n$ok = 0;\n\n// Get the other information from the form\n$name = mysql_real_escape_string($_POST['nameMember']);\n$bandMember = mysql_real_escape_string($_POST['bandMember']);\n$about = mysql_real_escape_string($_POST['aboutMember']);\n$bands = mysql_real_escape_string($_POST['otherBands']);\n\n// Check the file type\n$uploaded_type = $_FILES['photo']['type'];\nif ($uploaded_type == "image/jpeg" || $uploaded_type == "image/gif") {\n    $ok = 1;\n} else {\n    echo "Only JPEG and GIF files are allowed.";\n}\n\n// Check if a file was uploaded\nif (empty($_FILES['photo']['name'])) {\n    echo "You haven't uploaded a photo, a default will be used instead.";\n    $ok = 1;\n}\n\n// Check the file size\n$uploaded_size = $_FILES['photo']['size'];\nif ($uploaded_size > 350000) {\n    echo "Your file is too large, 35Kb is the largest file you can upload.";\n    $ok = 0;\n}\n\nif ($ok == 0) {\n    echo "Sorry your file was not uploaded, please try again with the correct format.";\n} else {\n    // Connect to the database\n    mysql_connect("localhost", "*******", "******") or die(mysql_error());\n    mysql_select_db("project") or die(mysql_error());\n\n    // Write the information to the database\n    mysql_query("INSERT INTO dbProfile (nameMember, bandMember, photo, aboutMember, otherBands) VALUES ('$name', '$bandMember', '".$_FILES['photo']['name']."', '$about', '$bands')");\n\n    // Write the photo to the server\n    if (move_uploaded_file($_FILES['photo']['tmp_name'], $target)) {\n        echo "The file ".basename($_FILES['photo']['name'])." has been uploaded, and your information has been added to the directory";\n    } else {\n        echo "There was a problem uploading your file.";\n    }\n}