function ping($host, $port = 80, $timeout = 10) {\n    $starttime = microtime(true);\n    $fp = @fsockopen($host, $port, $errno, $errstr, $timeout);\n    $stoptime  = microtime(true);\n    $status = 0;\n    if (!$fp) {\n        $status = -1;\n        $return = "Offline";\n    } else {\n        $status = 1;\n        $return = "Online. Time: " . ($stoptime - $starttime) * 1000 . " ms";\n        fclose($fp);\n    }\n    return $return;\n}