$stmt = $pdo->prepare('SELECT * FROM mytable');\n$stmt->execute();\n\n$result = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n// iterate over columns and use PDO::PARAM_* constants\nforeach ($result as $row) {\n    foreach ($row as $col => $value) {\n        switch ($stmt->getColumnMeta($col)['native_type']) {\n            case 'integer':\n                $value = (int) $value;\n                break;\n            case 'string':\n                $value = (string) $value;\n                break;\n            // ...\n        }\n    }\n}