$file = $_FILES['file']['tmp_name'];\n$destination = 'path/to/permanent/location/'.$_FILES['file']['name'];\n\nif (move_uploaded_file($file, $destination)) {\n    // File has been fully uploaded to Server A\n    // Now you can upload to Server B using CURL\n    $ch = curl_init('http://serverb.com/upload');\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n    curl_setopt($ch, CURLOPT_POST, true);\n    curl_setopt($ch, CURLOPT_POSTFIELDS, array('file' => '@'.$destination));\n    $response = curl_exec($ch);\n    curl_close($ch);\n} else {\n    // Handle upload error\n}