// Assuming you have a 'threads' table with a 'post_count' column\n// and a 'posts' table with a 'thread_id' column\n\n// When a new post is made\n$threadId = $_POST['thread_id'];\n$db = new PDO('mysql:host=localhost;dbname=yourdb', 'youruser', 'yourpass');\n\n// Update the post count using a prepared statement\n$stmt = $db->prepare('UPDATE threads SET post_count = post_count + 1 WHERE id = ?');\n$stmt->execute([$threadId]);\n\n// To retrieve the post count\n$stmt = $db->prepare('SELECT post_count FROM threads WHERE id = ?');\n$stmt->execute([$threadId]);\n$postCount = $stmt->fetchColumn();