$url = 'https://example.com/form-page';\n\n// Use CURL to get the HTML content\n$ch = curl_init($url);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n$html = curl_exec($ch);\ncurl_close($ch);\n\n// Create a DOMDocument object\n$doc = new DOMDocument();\n$doc->loadHTML($html);\n\n// Create a DOMXPath object\n$xpath = new DOMXPath($doc);\n\n// Find the form elements\n$formElements = $xpath->query('//form//input | //form//select | //form//textarea');\n\n// Serialize the form data\n$formData = array();\nforeach ($formElements as $element) {\n    $name = $element->getAttribute('name');\n    $value = $element->getAttribute('value');\n    if ($name && $value) {\n        $formData[$name] = $value;\n    }\n}\n\n// Print the serialized form data\nprint_r($formData);\n\n// You can now modify the form data as needed\n// ...\n\n// Send the modified form data using CURL\n$ch = curl_init($url);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_POST, true);\ncurl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($formData));\n$response = curl_exec($ch);\ncurl_close($ch);