// Application\View\Helper\Calendar.php\n\nnamespace Application\View\Helper;\n\nuse Zend\View\Helper\AbstractHelper;\nuse DateTime;\n\nclass Calendar extends AbstractHelper\n{\n    public function __invoke($year, $month)\n    {\n        $events = // retrieve events from database or other data source\n        $daysInMonth = date('t', mktime(0, 0, 0, $month, 1, $year));\n        $firstDayOfWeek = date('w', mktime(0, 0, 0, $month, 1, $year));\n\n        $html = '<table>';\n        $html .= '<tr>';\n        for ($i = 0; $i < $firstDayOfWeek; $i++) {\n            $html .= '<td>&nbsp;</td>';\n        }\n\n        for ($day = 1; $day <= $daysInMonth; $day++) {\n            $date = new DateTime("$year-$month-$day");\n            $hasEvent = false;\n            foreach ($events as $event) {\n                if ($event->getDate()->format('Y-m-d') == $date->format('Y-m-d')) {\n                    $hasEvent = true;\n                    break;\n                }\n            }\n            $html .= '<td' . ($hasEvent ? ' style="background-color: #ccc;"' : '') . '>' . $day . '</td>';\n            if (($day + $firstDayOfWeek) % 7 == 0) {\n                $html .= '</tr><tr>';\n            }\n        }\n\n        $html .= '</tr>';\n        $html .= '</table>';\n\n        return $html;\n    }\n}