function deleteUsers($userIDs) {\n    $db = new mysqli("your_host", "your_username", "your_password", "your_database");\n\n    if ($db->connect_error) {\n        return "Connection failed: " . $db->connect_error;\n    }\n\n    $stmt = $db->prepare("DELETE FROM members WHERE member_id = ?");\n\n    foreach ($userIDs as $userID) {\n        $stmt->bind_param("i", $userID);\n        $stmt->execute();\n    }\n\n    $stmt->close();\n    $db->close();\n\n    return 'yes';\n}