class A {\n    function fn() {\n        print 'Context: Class:' . get_class($this) \n                . ' Parent:' . get_parent_class($this) . "\n";\n        $parentClass = get_parent_class($this);\n        if($parentClass) {\n            call_user_func(array($this, 'parent::' . __FUNCTION__));\n        }\n    }\n}\n\nclass B extends A { }\nclass C extends B { }\n\n$a = new A();\n$c = new C();\n\n$a->fn();\n\nprint "\n";\n\n$c->fn();