require_once 'openid.php';\n\n$consumer = new OpenID_Consumer();\n\nif ($_POST['openid_url']) {\n  $openid_url = $_POST['openid_url'];\n  $consumer->begin($openid_url);\n  header('Location: ' . $consumer->getAuthURL());\n  exit;\n}\n\nif ($_GET['openid.mode'] == 'id_res') {\n  $consumer->complete($_GET['openid.return_to']);\n  $openid_identity = $consumer->getIdentity();\n  // Store $openid_identity in your database\n  echo 'OpenID authentication successful!';\n}