use Firebase\JWT\JWT;\n\n// Login endpoint (POST)\nfunction login($username, $password) {\n    // Verify credentials\n    if (/* valid credentials */) {\n        // Generate JWT token\n        $token = JWT::encode([\n            'iss' => 'your_issuer',\n            'exp' => time() + 3600, // 1 hour expiration\n            'username' => $username,\n        ], 'your_secret_key', 'HS256');\n\n        return [\n            'token' => $token,\n        ];\n    } else {\n        return [\n            'error' => 'Invalid credentials',\n        ];\n    }\n}\n\n// API endpoint (using token-based authentication)\nfunction apiEndpoint($request) {\n    $token = $request->getHeader('Authorization');\n    try {\n        // Verify token\n        $decoded = JWT::decode($token, 'your_secret_key', ['HS256']);\n        // Process request\n    } catch (Exception $e) {\n        return [\n            'error' => 'Invalid token',\n        ];\n    }\n}