require_once 'vendor/autoload.php';\n\nuse PhpIif\IifWriter;\n\n// Create a new IIF writer\n$writer = new IifWriter();\n\n// Add a customer record\n$customer = [\n    'NAME' => 'John Doe',\n    'COMPANYNAME' => 'Doe Inc.',\n    'ADDRESS1' => '123 Main St',\n    'ADDRESS2' => '',\n    'ADDRESS3' => '',\n    'CITY' => 'Anytown',\n    'STATE' => 'CA',\n    'ZIP' => '12345',\n];\n\n$writer->addCustomer($customer);\n\n// Add a sales record\n$sales = [\n    'DATE' => '2023-02-20',\n    'INVOICE' => ' INV001',\n    'CUSTOMER' => 'John Doe',\n    'ITEM' => ' Item1',\n    'QUANTITY' => 2,\n    'RATE' => 10.99,\n];\n\n$writer->addSales($sales);\n\n// Save the IIF file\n$writer->save('output.iif');