// auth.php\n\n// Set the password and username\n$username = 'admin';\n$password = 'password';\n\n// Start the session\nsession_start();\n\n// Check if the user is already logged in\nif (isset($_SESSION['logged_in']) && $_SESSION['logged_in'] === true) {\n    // If logged in, redirect to the protected directory\n    header('Location: protected_directory/index.php');\n    exit;\n}\n\n// Check if the login form has been submitted\nif (isset($_POST['username']) && isset($_POST['password'])) {\n    // Check the credentials\n    if ($_POST['username'] === $username && $_POST['password'] === $password) {\n        // Set the logged in session variable\n        $_SESSION['logged_in'] = true;\n        // Redirect to the protected directory\n        header('Location: protected_directory/index.php');\n        exit;\n    } else {\n        // Display an error message\n        echo 'Invalid username or password';\n    }\n}\n\n// Display the login form\necho '<form action="" method="post">';\necho '<label for="username">Username:</label>';\necho '<input type="text" id="username" name="username"><br><br>';\necho '<label for="password">Password:</label>';\necho '<input type="password" id="password" name="password"><br><br>';\necho '<input type="submit" value="Login">';\necho '</form>';