use PHPSQLParser\PHPSQLParser;\n\nfunction rewriteQueryForCount($query) {\n    $parser = new PHPSQLParser();\n    $parsedQuery = $parser->parse($query);\n\n    // Replace the SELECT clause with COUNT(*)\n    $parsedQuery['SELECT'] = array('COUNT(*)');\n\n    // Rebuild the query\n    $countQuery = $parser->format($parsedQuery);\n\n    return $countQuery;\n}\n\n// Example usage:\n$query = "SELECT a, b, c FROM table_name WHERE condition GROUP BY a, b, c";\n$countQuery = rewriteQueryForCount($query);\necho $countQuery; // Output: "SELECT COUNT(*) FROM table_name WHERE condition GROUP BY a, b, c"