// Container class\nclass Container {\n    private $database;\n    private $userAuth;\n\n    public function __construct() {\n        $this->database = Database::getInstance();\n        $this->userAuth = UserAuth::getInstance();\n    }\n\n    public function getDatabase() {\n        return $this->database;\n    }\n\n    public function getUserAuth() {\n        return $this->userAuth;\n    }\n}