// Login function\nfunction login($username, $password) {\n  // Verify username and password\n  $user_id = verify_credentials($username, $password);\n  if ($user_id) {\n    // Generate a random session ID\n    $session_id = bin2hex(random_bytes(32));\n    // Store the session ID in the database linked to the user's account\n    store_session_id($user_id, $session_id);\n    // Set the session ID as a cookie\n    setcookie('session_id', $session_id, time() + 3600, '/');\n    return true;\n  }\n  return false;\n}\n\n// Function to verify if a user is logged in\nfunction is_logged_in() {\n  // Check if the session ID cookie is set\n  if (isset($_COOKIE['session_id'])) {\n    // Get the session ID from the cookie\n    $session_id = $_COOKIE['session_id'];\n    // Verify the session ID in the database\n    $user_id = verify_session_id($session_id);\n    if ($user_id) {\n      return true;\n    }\n  }\n  return false;\n}