function priceDisplay($baseCurrencyAmounts) {\n    $currencyRate = $_SESSION['currencyRate'];\n    $total = 0;\n    foreach ($baseCurrencyAmounts as $amount) {\n        $convertedAmount = round($amount * $currencyRate, 2);\n        $total += $convertedAmount;\n    }\n    $convertedTotal = round($total, 2);\n    // Adjust the total to match the sum of the converted amounts\n    if ($convertedTotal != $total) {\n        $total = $convertedTotal;\n    }\n    return array(\n        'convertedAmounts' => array_map(function($amount) use ($currencyRate) {\n            return round($amount * $currencyRate, 2);\n        }, $baseCurrencyAmounts),\n        'convertedTotal' => $total\n    );\n}\n\n// Example usage:\n$baseCurrencyAmounts = array(9.75, 5.95);\n$result = priceDisplay($baseCurrencyAmounts);\nprint_r($result);