$serverFields = [\n    'SERVER_ID',\n    'SERVERNAME',\n    'SERVERLOCATION_ID',\n    'SERVERLOCATIONDETAIL_ID',\n    'SERVEROS_ID',\n    'SERVEROSVERSION_ID',\n    'IP_BACKEND',\n    'IP_FRONTEND',\n    'IP_BACKUP',\n    'SERVERREMOTETOOL_IDS',\n    'LOGIN',\n    'DESCRIPTION',\n    'TIME_INSERT',\n    'TIME_UPDATE',\n    'CHANGE_USER',\n    'MANDATOR_ID',\n    'TIVOLI_UPDATABLE',\n    'TIVOLI_LAST_SCAN',\n    'TIVOLI_LAST_UPDATE',\n    'WINDOMAIN_ID',\n    'PROCESSORCOUNT',\n    'OS_ID',\n    'PHYSICAL_MEMORY_KB'\n];\n\n$osFields = [\n    'OSLANG_ID',\n    'OSARCH_ID',\n    'OSSUBVERSION_ID',\n    'OSMINORVERSION_ID',\n    'OSMAJORVERSION_ID',\n    'OSTYPE_ID'\n];\n\n$processorFields = [\n    'PROCESSORMODEL_ID',\n    'PROCESSORMANUFACTURER_ID'\n];\n\n$sql = sprintf(\n    "SELECT \n        %s,\n        %s,\n        %s\n    FROM %s s\n    LEFT OUTER JOIN (\n        (OS \n            JOIN OSSUBVERSION USING (OSSUBVERSION_ID)\n            JOIN OSMINORVERSION USING (OSMINORVERSION_ID)\n            JOIN OSMAJORVERSION USING (OSMAJORVERSION_ID)\n        ) USING (OS_ID)\n    )\n    LEFT OUTER JOIN (\n        PROCESSORMODEL \n            JOIN PROCESSORMANUFACTURER USING(PROCESSORMANUFACTURER_ID)\n    ) USING (PROCESSORMODEL_ID)\n    WHERE %s = :id AND MANDATOR_ID = :mandatorId",\n    implode(', ', $serverFields),\n    implode(', ', $osFields),\n    implode(', ', $processorFields),\n    $this->_tableName,\n    $this->_identifier\n);