// Create a base controller, e.g., MY_Controller.php\nclass MY_Controller extends CI_Controller {\n    protected $data = array();\n\n    public function __construct() {\n        parent::__construct();\n        $this->data['header'] = $this->load->view('includes/header','',true);\n        $this->data['scripts'] = $this->load->view('includes/scripts','',true);\n        $this->data['navigation'] = $this->load->view('includes/navigation','',true);\n        $this->data['footer'] = $this->load->view('includes/footer','',true);\n    }\n}