class Foo {\n    public $name = 'Andrew';\n\n    public function getName() {\n        echo $this->name;\n    }\n}\n\nclass Bar extends Foo {\n    public $name = 'John';\n\n    public function getName() {\n        Foo::getName(); // This creates a temporary instance of Bar!\n    }\n}\n\n$a = new Bar;\n$a->getName(); // Outputs "John"\n\n// But this will throw a fatal error:\nFoo::getName(); // No instance of Foo to bind to