$items = array(\n    "1",\n    "2",\n    array(\n        "item3.1" => "3.1",\n        "item3.2" => "3.2",\n        "isawesome" => true\n    )\n);\n\n$xml = new SimpleXMLElement('<items/>');\nforeach ($items as $item) {\n    if (is_array($item)) {\n        $child = $xml->addChild('item');\n        foreach ($item as $key => $value) {\n            $child->addChild(str_replace('.', '_', $key), $value);\n        }\n    } else {\n        $xml->addChild('item', $item);\n    }\n}\n\n$xmlString = $xml->asXML();\n\necho $xmlString;\n\n// And to convert it back to an array\n$xml = simplexml_load_string($xmlString);\n$array = json_decode(json_encode($xml), true);\n\nprint_r($array);