// Supported formats:\n$d = strtotime("10 September 2000"); // "YYYY-MM-DD" format\n$d = strtotime("2000-09-10"); // ISO 8601 format\n$d = strtotime("20000910"); // ISO 8601 format without hyphens\n$d = strtotime("Sep 10, 2000"); // "MMM DD, YYYY" format\n$d = strtotime("September 10, 2000"); // Full month name\n$d = strtotime("next Thursday"); // Relative date format\n$d = strtotime("+1 week 2 days 4 hours 2 seconds"); // Relative date format\n$d = strtotime("yesterday"); // Relative date format\n$d = strtotime("now"); // Current date and time