$hours = 04;\n$minutes = 45;\n$ampm = 'PM';\n\n// Convert hours to 24-hour format\nif ($ampm == 'PM' && $hours != 12) {\n    $hours += 12;\n} elseif ($ampm == 'AM' && $hours == 12) {\n    $hours = 0;\n}\n\n// Format the time as a MySQL-compatible DATETIME string\n$date = date('Y-m-d'); // Assuming current date\n$time = sprintf('%02d:%02d:00', $hours, $minutes);\n$datetime = $date . ' ' . $time;\n\necho $datetime;