function drawPixels($binaryData, $width, $height) {\n    $pixels = '';\n    $byteIndex = 0;\n\n    for ($y = 0; $y < $height; $y++) {\n        for ($x = 0; $x < $width; $x++) {\n            if ($byteIndex >= strlen($binaryData)) {\n                break;\n            }\n\n            $byte = ord($binaryData[$byteIndex++]);\n            $red = ($byte >> 0) & 0x07; // 3 bits\n            $green = ($byte >> 3) & 0x07; // 3 bits\n            $blue = ($byte >> 6) & 0x03; // 2 bits\n\n            // Scale up the color values to 0-255 range\n            $red = $red * 32;\n            $green = $green * 32;\n            $blue = $blue * 64;\n\n            $pixels .= 'ctx.fillStyle = "rgb(' . $red . ',' . $green . ',' . $blue . ')";';\n            $pixels .= 'ctx.fillRect(' . $x . ',' . $y . ',1,1);';\n        }\n    }\n\n    return $pixels;\n}\n\n// Example usage\n$binaryData = '... your binary data string ...';\n$width = 320;\n$height = 240;\n\n$pixels = drawPixels($binaryData, $width, $height);\n\necho $pixels;