$flickr_content = '<p><a href="http://www.flickr.com/people/19725893@N00/">codewrecker</a> posted a photo:</p>\n<p><a href="http://www.flickr.com/photos/19725893@N00/2302759205/" title="Santa Monica Pier"><img src="http://farm3.static.flickr.com/2298/2302759205_4fb109f367_m.jpg" width="180" height="240" alt="Santa Monica Pier" /></a></p>';\n\n$pattern = '/<img src="([^"]+)"/';\npreg_match($pattern, $flickr_content, $matches);\n\n$img_url = $matches[1];\necho $img_url; // outputs: http://farm3.static.flickr.com/2298/2302759205_4fb109f367_m.jpg